		select service_id, sum(total_days/60) as total
		from cte
		group by 1
	),
	cte3 as (
		select service.image as image, service.service_name as name, round(cte2.total, 2) as time_spent, round(cte2.total * ((service.subscription_price/30)/24), 4) as cost
		from cte2
		join service on
			cte2.service_id = service.service_id
	)
	select image, name, time_spent
	from cte3
	order by time_spent desc
	limit 1;
end ;;
